Summary:
{{ $json.summaryText }}

parameters.jsCode:
// Parse the raw LLM string output (stored in $json.text)
const rawText = $json.text;

// Extract only the JSON string part (if it includes "json[...]" or any wrapper)
const jsonStart = rawText.indexOf('[');
const jsonEnd = rawText.lastIndexOf(']');
const jsonString = rawText.slice(jsonStart, jsonEnd + 1);

// Parse the array
let tasks;
try {
  tasks = JSON.parse(jsonString);
} catch (err) {
  throw new Error("Failed to parse JSON array from LLM output: " + err.message);
}

// Map it to the desired Google Sheets format
return tasks.map(task => {
  return {
    json: {
      Task: task.task,
      Owner: task.owner,
      Category: task.category,
      Deadline: task.deadline,
      Status: task.status,
      File: task.file || ''
    }
  };
});

// Combine all task rows into one message string
const tasks = $input.all();

const sheetUrl = 'https://docs.google.com/spreadsheets/d/1BiKlMrA05BCpOaUYOur4czHWT0PAR6iQxpBBIKzaipl/edit#gid=1779524410'; // replace with your actual sheet URL

let message = `Hello,\n\nThe following tasks were added to the project tracker:\n\n`;

message += tasks.map(item => {
  const t = item.json;
  return `â€¢ ${t.Task} (Owner: ${t.Owner}, Category: ${t.Category}, Deadline: ${t.Deadline}, Status: ${t.Status})`;
}).join('\n');

message += `\n\nYou can view the full sheet here:\n${sheetUrl}\n\nRegards,\nn8n Automation`;

return [
  {
    json: {
      emailBody: message
    }
  }
];

